					["Hooks"] = {
						["Nameplate Created"] = "function (self, unitId, unitFrame, envTable)\n    \n    -- @unitId  unitID for mob e.g nameplate1\n    -- @marker Raid Target ID\n    -- @nameColouring Enables text to be coloured by raid marker \n    -- @isBoss Boolean for enabling this on boss mobs\n    -- @debugMode Test mode for using dummy's\n    -- @debugEntry Which hook it came from\n    local mark = unitId and GetRaidTargetIndex(unitId)\n    envTable.namer(unitId, mark, true, false, false, \"Created\")\n    \nend",
						["Nameplate Updated"] = "function (self, unitId, unitFrame, envTable)\n    -- @unitId  unitID for mob e.g nameplate1\n    -- @marker Raid Target ID\n    -- @nameColouring Enables text to be coloured by raid marker \n    -- @isBoss Boolean for enabling this on boss mobs\n    -- @debugMode Test mode for using dummy's\n    -- @debugEntry Which hook it came from\n    -- Catch all \n    envTable.namer(unitId, GetRaidTargetIndex(unitId), true, false, false, \"Updated\")\n    \nend\n\n\n",
						["Nameplate Added"] = "function (self, unitId, unitFrame, envTable)\n    -- @unitId  unitID for mob e.g nameplate1\n    -- @marker Raid Target ID\n    -- @nameColouring Enables text to be coloured by raid marker \n    -- @isBoss Boolean for enabling this on boss mobs\n    -- @debugMode Test mode for using dummy's\n    -- @debugEntry Which hook it came from\n    envTable.namer(unitId, GetRaidTargetIndex(unitId), true, false, false, \"Added\")\n    \nend\n\n\n",
						["Constructor"] = "function (self, unitId, unitFrame, envTable)\n    -- used for nameColouring\n    -- AARRGGBB\n    local markerToHex = {\n        [1] = \"FFEAEA0D\", -- Yellow 5 Point Star\n        [2] = \"FFEAB10D\", -- Orange Circle\n        [3] = \"FFCD00FF\", -- Purple Diamond\n        [4] = \"FF06D425\", -- Green Triangle\n        [5] = \"FFB3E3D8\", -- Light Blue Moon\n        [6] = \"FF0CD2EA\", -- Blue Square\n        [7] = \"FFD6210B\", -- Red Cross\n        [8] = \"FFFFFFFF\", -- White Skull\n    }\n    \n    -- Makes it so you take their first name e.g Jessie Howlis -> Jessie\n    -- Old way was some degen fucking shit this is easier\n    local nameBlacklist = {\n        [\"the\"] = true,\n        [\"of\"] = true,\n        [\"Tentacle\"] = true,\n        [\"Apprentice\"] = true,  \n        [\"Denizen\"] = true,\n        [\"Emissary\"] = true,\n        [\"Howlis\"] = true, \n        [\"Terror\"] = true, \n        [\"Totem\"] = true, \n        [\"Waycrest\"] = true,\n        [\"Dummy\"] = true, -- Testing Purposes\n        [\"Aspect\"] = true\n    }\n    \n    \n    -- Override for names, Thanks to Nnoggie for all of the dungeon ones\n    local renameTable = {\n        -- Testing\n        [\"Fleshripper Vulture\"] = \"Shit Bird\",\n        [\"Roaming Nibbler\"] = \"Roaming Saurid\",\n        -- Grand Delusions\n        [\"Thing From Beyond\"] = \"Ion Hazzikostas\",\n        -- The Eternal Palace\n        [\"Abyssal Commander Sivara\"] = \"Sivara\",\n        [\"Blackwater Behemoth\"] = \"Behemoth\",\n        [\"Radiance of Azshara\"] = \"Radiance\",\n        [\"Lady Ashvane\"] = \"Ashvane\",\n        [\"Orgozoa\"] = \"Orgozoa\",\n        [\"Silivaz the Zealous\"] = \"Silivaz\",\n        [\"Pashmar the Fanatical\"] = \"Pashmar\",\n        [\"Za'qul, Harbinger of Ny'alotha\"] = \"Za'qul\",\n        [\"Queen Azshara\"] = \"Azshara\",\n        [\"Thing From Beyond\"] = \"Ion Hazzikostas\",\n        --Awakened (BfA S4 Affix)\n        [\"Voidweaver Mal'thir\"] = \"Voidweaver Mal'thir\",\n        [\"Samh'rek, Beckoner of Chaos\"] = \"Samh'rek\",\n        [\"Urg'roth, Breaker of Heroes\"] = \"Urg'roth\",\n        [\"Blood of the Corruptor\"] = \"Blood of the Corruptor\",\n        --Atal'Dazar\n        [\"Reanimated Honor Guard\"] = \"Honor Guard\",\n        [\"Dinomancer Kish'o\"] = \"Dinomancer\",\n        [\"Dazar'ai Honor Guard\"] = \"Honor Guard\",\n        [\"Shieldbearer of Zul\"] = \"Shieldbearer\",\n        --Freehold\n        [\"Cutwater Knife Juggler\"] = \"Knife Juggler\",\n        [\"Skycap'n Kragg\"] = \"Skycap'n Kragg\",\n        [\"Harlan Sweete\"] = \"Harlan Sweete\",\n        --King's Rest\n        [\"Minion of Zul\"] = \"Minion\",\n        [\"Shadow-Borne Witch Doctor\"] = \"Witch Doctor\",\n        [\"Queen Wasi\"] = \"Queen\",\n        [\"King Timalji\"] = \"King\",\n        [\"Queen Patlaa\"] = \"Queen\",\n        [\"King A'akul\"] = \"King\",\n        [\"Shieldbearer of Zul\"] = \"Shieldbearer\",\n        [\"Seneschal M'bara\"] = \"Seneschal M'bara\",\n        [\"King Rahu'ai\"] = \"King\",\n        [\"Guard Captain Atu\"] = \"Guard Captain\",\n        [\"Mchimba the Embalmer\"] = \"Mchimba\",\n        [\"Spectral Hex Priest\"] = \"Hex Priest\",\n        [\"The Golden Serpent\"] = \"The Golden Serpent\",\n        [\"Kula the Butcher\"] = \"Kula the Butcher\",\n        [\"Zanazal the Wise\"] = \"Zanazal the Wise\",\n        [\"Aka'ali the Conqueror\"] = \"Aka'ali\",\n        [\"King Dazar\"] = \"King Dazar\",\n        --Shrine of the Storm\n        [\"Windspeaker Heldis\"] = \"Windspeaker\",\n        [\"Runecarver Sorn\"] = \"Runecarver\",        \n        [\"Vol'zith the Whisperer\"] = \"Vol'zith\",\n        [\"Living Current\"] = \"Living Current\",\n        [\"Galecaller Faye\"] = \"Galecaller Faye\",\n        [\"Brother Ironhull\"] = \"Brother Ironhull\",\n        [\"Sunken Denizen\"] = \"Sunken Denizen\",\n        [\"Forgotten Denizen\"] = \"Forgotten Denizen\",\n        --Siege of Boralus\n        [\"Gripping Terror\"] = \"Gripping\",\n        [\"Demolishing Terror\"] = \"Demolishing\",\n        [\"Sergeant Bainbridge\"] =\"Sergeant Bainbridge\",\n        [\"Dread Captain Lockwood\"] = \"Dread Captain Lockwood\",\n        [\"Hadal Darkfathom\"] = \"Hadal Darkfathom\",\n        --Temple of Sethraliss\n        [\"Charged Dust Devil\"] = \"Dust Devil\",\n        [\"Shrouded Fang\"] = \"Shrouded Fang\",\n        [\"Spark Channeler\"] = \"Spark Channeler\",\n        --The MOTHERLODE!!\n        [\"Test Subject\"] = \"Rat\",\n        [\"Coin-Operated Crowd Pummeler\"] = \"Crowd Pummeler\",\n        [\"Rixxa Fluxflame\"] = \"Rixxa Fluxflame\",\n        [\"Mogul Razdunk\"] = \"Mogul Razdunk\",\n        --The Underrot\n        [\"Elder Leaxa\"] = \"Elder Leaxa\",\n        [\"Cragmaw the Infested\"] = \"Cragmaw\",\n        [\"Sporecaller Zancha\"] = \"Sporecaller\",\n        [\"Unbound Abomination\"] = \"Unbound Abomination\",\n        --Tol Dagor\n        [\"The Sand Queen\"] = \"The Sand Queen\",\n        [\"Block Warden\"] = \"Block Warden\",\n        [\"Jes Howlis\"] = \"Jes\",\n        [\"Bobby Howlis\"] = \"Bobby\",\n        [\"Knight Captain Valyri\"] = \"Knight Captain Valyri\",\n        [\"Overseer Korgus\"] = \"Overseer Korgus\",\n        --Waycrest Manor\n        [\"Soul Essence\"] = \"Soul Essence\",\n        [\"Soulbound Goliath\"] = \"Soulbound Goliath\",\n        [\"Raal the Gluttonous\"] = \"Raal the Gluttonous\",\n        [\"Gorak Tul\"] = \"Gorak Tul\",\n        --Operation: Mechagon (Junkyard)\n        [\"Malfunctioning Scrapbot\"] = \"Malfunctioning Scrapbot\",\n        [\"Heavy Scrapbot\"] = \"Heavy Scrapbot\",\n        [\"King Gobbamak\"] = \"King Gobbamak\",\n        [\"Trixie Tazer\"] = \"Trixie Tazer\",\n        [\"Naeno Megacrash\"] = \"Naeno Megacrash\",\n        [\"HK-8 Aerial Oppression Unit\"] = \"HK-8 Aerial Oppression Unit\",\n        [\"Tank Buster MK1\"] = \"Tank Buster MK1\",\n        --Operation: Mechagon (Workshop)\n        [\"Strider Tonk\"] = \"Strider\",\n        [\"Bomb Tonk\"] = \"Bomb\",\n        [\"Rocket Tonk\"] = \"Rocket\",\n        [\"Gnomercy 4.U.\"] = \"Gnomercy 4.U.\",\n        [\"The Platinum Pummeler\"] = \"The Platinum Pummeler\",\n        [\"Waste Processing Unit\"] = \"Waste Processing Unit\",\n        [\"Blastatron X-80\"] = \"Blastatron\",\n        [\"Defense Bot Mk I\"] = \"Defense Bot Mk One\",\n        [\"Defense Bot Mk III\"] = \"Defense Bot Mk Three\",\n        [\"Spider Tank\"] = \"Spider Tank\",\n        [\"Aerial Unit R-21/X\"] = \"Aerial Unit R-21/X\",\n        [\"Omega Buster\"] = \"Omega Buster\",\n        \n        \n    }\n    \n    \n    \n    -- @unitId  unitID for mob e.g nameplate1\n    -- @marker Raid Target ID\n    -- @nameColouring Enables text to be coloured by raid marker \n    -- @isBoss Boolean for enabling this on boss mobs, Do i want this?? no idea\n    -- @debugMode Test mode for using dummy's\n    -- @debugEntry Which hook it came from\n    function envTable.namer(unitId, marker, nameColouring, isBoss, debugMode, debugEntry)\n        if unitId then\n            \n            local name = UnitName(unitId)\n            local a, b, c, d, e, f = strsplit(' ', name, 5)\n            local unitName\n            -- Testing mode\n            if debugMode then\n                print(\"DebugMode: \"..debugEntry..\" - Checking for dummy name\")\n                if b == \"Dummy\" or c == \"Dummy\" or d == \"Dummy\" or e == \"Dummy\" or f == \"Dummy\" then\n                    local lastName = b:find(\"Dummy\") and b or c:find(\"Dummy\") and c or d:find(\"Dummy\") and d\n                    print(\"DebugMode: \"..debugEntry..\" - Dummy Found!\")\n                    unitName = name ~=nil and lastName\n                end\n            end\n            \n            \n            if nameBlacklist[b] then\n                unitName = name ~=nil and (a or b or c or d or e or f) or nil\n            else\n                unitName = name ~=nil and (f or e or d or c or b or a) or ni\n            end\n            \n            if unitId and marker == nil and nameColouring then\n                marker = 8\n            end\n            \n            \n            \n            if unitName == nil then\n                unitName = name\n            end\n            \n            \n            if renameTable[name] then\n                unitName = renameTable[name]\n            end\n            \n            if unitId and marker and nameColouring then\n                unitFrame.healthBar.unitName:SetText(WrapTextInColorCode(unitName, markerToHex[marker]))\n            elseif unitId then\n                unitFrame.healthBar.unitName:SetText(unitName)\n            end\n        end   \n    end\nend",
